options.customCss:
:root {
  --font-family: 'Inter', sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 600;
  --font-size-body: 14px;
  --font-size-label: 14px;
  --font-size-test-notice: 14px;
  --font-size-input: 14px;
  --font-size-header: 20px;
  --font-size-paragraph: 14px;
  --font-size-link: 14px;
  --font-size-error: 12px;
  --font-size-html-h1: 24px;
  --font-size-html-h2: 20px;
  --font-size-html-h3: 16px;
  --font-size-html-h4: 14px;
  --font-size-html-h5: 12px;
  --font-size-html-h6: 10px;
  --font-size-subheader: 14px;

	/* Colors */
  --color-background: #FFFFFF;
  --color-test-notice-text: #1EAEDB;
  --color-test-notice-bg: #f0f9ff;
  --color-test-notice-border: #bae6fd;
  --color-card-bg: #FFFFFF;
  --color-card-border: #e2e8f0;
  --color-card-shadow: rgba(30, 174, 219, 0.06);
  --color-link: #8E9196;
  --color-header: #333333;
  --color-label: #555555;
  --color-input-border: #e2e8f0;
  --color-input-text: #4a5568;
  --color-focus-border: #1EAEDB;
  --color-submit-btn-bg: #1EAEDB;
  --color-submit-btn-text: #FFFFFF;
  --color-error: #ef4444;
  --color-required: #1EAEDB;
  --color-clear-button-bg: #8E9196;
  --color-html-text: #555555;
  --color-html-link: #1EAEDB;
  --color-header-subtext: #8E9196;

	/* Border Radii */
  --border-radius-card: 0.75rem;
  --border-radius-input: 0.5rem;
  --border-radius-clear-btn: 50%;
  --card-border-radius: 0.75rem;

	/* Spacing */
  --padding-container-top: 0px;
  --padding-card: 24px;
  --padding-test-notice-vertical: 12px;
  --padding-test-notice-horizontal: 24px;
  --margin-bottom-card: 16px;
  --padding-form-input: 12px;
  --card-padding: 24px;
  --card-margin-bottom: 16px;

	/* Dimensions */
  --container-width: 100%;
  --submit-btn-height: 48px;
  --checkbox-size: 18px;

	/* Others */
  --box-shadow-card: 0px 4px 16px 0px rgba(30, 174, 219, 0.06);
  --opacity-placeholder: 0.5;
}
