const maxChunkSize = 1000;
const chunkOverlap = 200;

const text = $json.data;

// Función para dividir el texto sin cortar palabras
function splitTextByWords(text, maxChunkSize, chunkOverlap) {
	const words = text.split(/\s+/); // divide por espacios y saltos de línea
	const chunks = [];

	let chunk = '';
	for (let i = 0; i < words.length; i++) {
		const word = words[i];
		if ((chunk + ' ' + word).length <= maxChunkSize) {
			chunk += (chunk ? ' ' : '') + word;
		} else {
			chunks.push(chunk.trim());

			// Generar la superposición basada en palabras desde el final del chunk anterior
			const chunkWords = chunk.split(/\s+/);
			const overlapWords = chunkWords.slice(-Math.floor(chunkOverlap / 5)); // estimamos 5 caracteres por palabra
			chunk = overlapWords.join(' ') + ' ' + word;
		}
	}
	if (chunk) chunks.push(chunk.trim());

	return chunks;
}

const resultChunks = splitTextByWords(text, maxChunkSize, chunkOverlap);

return resultChunks.map((chunk, index) => ({
	json: {
		chunkIndex: index + 1,
		text: chunk
	}
}));

const items = $input.all();
const categorias = {};

items.forEach(({ json }) => {
  const cat = json["Categoría"]?.trim();
  if (!cat) return;

  if (!categorias[cat]) categorias[cat] = [];

  categorias[cat].push({
    nombre: json["Nombre"]?.trim() || "",
    marca: json["Marca"]?.trim() || "",
    precio: Number(json["Precio (MXN)"] ?? 0),
    stock: Number(json["Stock"] ?? 0),
    peso: Number(json["Peso (g)"] ?? 0),
    sabor: json["Sabor"]?.trim() || "",
    formato: json["Formato"]?.trim() || ""
  });
});

return Object.entries(categorias).map(([categoria, productos]) => ({
  json: { categoria, productos }
}));

const items = $input.all();

return items.map(({ json }) => {
  const categoria = json.Categoria || json.categoria || "Sin categoría";
  const descripcion = json.text?.trim() || "";

  let productos;
  try {
    productos = JSON.parse(json.Productos);
  } catch (e) {
    throw new Error(`Productos en categoría "${categoria}" no es un JSON válido.`);
  }

  const lista = productos.map((p, i) => {
    return `${i + 1}. Nombre: ${p.nombre}, Marca: ${p.marca}, Precio: $${p.precio} MXN, Stock: ${p.stock}, Peso: ${p.peso}g, Sabor: ${p.sabor}, Formato: ${p.formato}`;
  }).join('\n');

  const textoFinal = `Categoría: ${categoria}\n\n${descripcion}\n\n${lista}`;

  return {
    json: {
      categoria,
      texto: textoFinal
    }
  };
});

const rows = $input.all();

const salida = rows.map(({ json }, i) => {
  const campos = Object.entries(json).map(([clave, valor]) => {
    return `${clave}: ${valor}`;
  }).join(', ');

  return `${i + 1}. ${campos}`;
});

return [
  {
    json: {
      data: salida.join('\n')
    }
  }
];
