// Obtener el tipo de reporte desde el agente
const reporte = $json.output?.reporte || 'ninguno';

// Teléfono del usuario (ajusta según tu flujo)
const telefono = $('set fields').first().json._validacion.numeroValidado;

// Función para obtener fecha en hora de Perú (UTC-5)
function getPeruDate() {
  const now = new Date();
  // Ajustar a UTC-5 (Perú)
  const peruTime = new Date(now.getTime() - (5 * 60 * 60 * 1000));
  return peruTime;
}

// Función para formatear fechas (YYYY-MM-DD)
function formatDate(date) {
  const year = date.getUTCFullYear();
  const month = String(date.getUTCMonth() + 1).padStart(2, '0');
  const day = String(date.getUTCDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

// Función para formatear fecha y hora (YYYY-MM-DD HH:MM:SS)
function formatDateTime(date) {
  const year = date.getUTCFullYear();
  const month = String(date.getUTCMonth() + 1).padStart(2, '0');
  const day = String(date.getUTCDate()).padStart(2, '0');
  const hours = String(date.getUTCHours()).padStart(2, '0');
  const minutes = String(date.getUTCMinutes()).padStart(2, '0');
  const seconds = String(date.getUTCSeconds()).padStart(2, '0');
  return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
}

const hoy = getPeruDate();
let fechaInicio, fechaFin;

switch (reporte) {
  case 'hoy':
    fechaInicio = formatDate(hoy);
    fechaFin = formatDate(hoy);
    break;
  case 'ayer':
    const ayer = new Date(hoy.getTime());
    ayer.setUTCDate(hoy.getUTCDate() - 1);
    fechaInicio = formatDate(ayer);
    fechaFin = formatDate(ayer);
    break;
  case 'semana':
    const inicioSemana = new Date(hoy.getTime());
    inicioSemana.setUTCDate(hoy.getUTCDate() - hoy.getUTCDay());
    fechaInicio = formatDate(inicioSemana);
    fechaFin = formatDate(hoy);
    break;
  case 'mes':
    const inicioMes = new Date(Date.UTC(hoy.getUTCFullYear(), hoy.getUTCMonth(), 1));
    fechaInicio = formatDate(inicioMes);
    fechaFin = formatDate(hoy);
    break;
  default:
    fechaInicio = null;
    fechaFin = null;
}

// Construir el WHERE según lo que pida el usuario
let where = `(telefono,eq,${telefono})`;
if (fechaInicio && fechaFin) {
  where += `~and(fecha,ge,exactDate,${fechaInicio})~and(fecha,le,exactDate,${fechaFin})`;
}

// Retornar el WHERE y la fecha/hora actual
return [{
  json: {
    ...$json,
    where,
    fechaHoraActual: formatDateTime(hoy)  // Ahora en hora de Perú
  }
}];

// Nodo Code — Generar respuesta de reporte (funciona con varios items entrantes)
// Obtener todos los items entrantes al nodo (cada item tiene .json)
const items = $input.all();
// Mapear a un array simple de objetos JSON
const data = items.map(i => i.json);

// Si no hay registros válidos
if (!Array.isArray(data) || data.length === 0 || Object.keys(data[0] || {}).length === 0) {
  return [{
    json: {
      ...$json,
      mensaje_usuario: "📭 No encontré gastos en ese período.",
      total: 0,
      resumen: {},
      registros: []
    }
  }];
}

// Función para convertir fecha de YYYY-MM-DD a DD/MM/YYYY
function formatearFecha(fecha) {
  if (!fecha) return '';

  // Si ya viene en formato DD/MM/YYYY, retornarla tal cual
  if (fecha.includes('/')) return fecha;

  // Convertir de YYYY-MM-DD a DD/MM/YYYY
  const partes = fecha.split('-');
  if (partes.length === 3) {
    return `${partes[2]}/${partes[1]}/${partes[0]}`;
  }

  return fecha; // Si no coincide con ningún formato, devolver original
}

// Calcular total y agrupar por categoría
let total = 0;
const resumen = {};

for (const g of data) {
  const monto = Number(g.monto) || 0;
  total += monto;
  const cat = g.categoria || "Sin categoría";
  resumen[cat] = (resumen[cat] || 0) + monto;
}

// Obtener periodo si existe (ej: 'hoy', 'ayer', etc.)
const periodo = $json.periodo || ($json.output && $json.output.reporte) || '';

// Construir mensaje
let mensaje = `📊 **Reporte de gastos${periodo ? ' ' + periodo : ''}**\n`;
mensaje += `Total general: 💰 ${total.toFixed(2)} soles\n\n`;
mensaje += `**Por categorías:**\n`;

for (const [categoria, suma] of Object.entries(resumen)) {
  mensaje += `- ${categoria}: ${suma.toFixed(2)} soles\n`;
}

mensaje += `\n🧾 **Detalles:**\n`;

for (const g of data) {
  const desc = g.descripcion || 'Sin descripción';
  const fecha = formatearFecha(g.fecha || '');
  mensaje += `• ${desc} — ${Number(g.monto).toFixed(2)} soles (${g.categoria || 'Sin categoría'}, ${fecha})\n`;
}

mensaje += `\nSe encontraron ${data.length} registros en total.`;

// Retornar resultado para siguientes nodos
return [{
  json: {
    ...$json,
    mensaje_usuario: mensaje,
    total,
    resumen,
    registros: data
  }
}];

