// Obtener el número del webhook (remover @s.whatsapp.net)
const numeroWebhook = $('recibe whatsapp').item.json.body.data.key.remoteJid.replace('@s.whatsapp.net', '');

// Obtener todos los números de la lista blanca
const numerosListaBlanca = $input.all().map(item => item.json.celular);

// Verificar si el número está en la lista blanca
const estaEnListaBlanca = numerosListaBlanca.includes(numeroWebhook);

// Obtener datos del webhook
const webhookData = $('recibe whatsapp').item.json.body;
const messageData = webhookData.data;

// Determinar el tipo de contenido
let contentType = '';
if (messageData.message.extendedTextMessage) contentType = 'text';
else if (messageData.message.conversation) contentType = 'text';
else if (messageData.message.audioMessage) contentType = 'audio';
else if (messageData.message.imageMessage) contentType = 'image';

// Obtener el contenido del mensaje
let messageContent = '';
if (messageData.message.extendedTextMessage?.text) {
  messageContent = messageData.message.extendedTextMessage.text;
} else if (messageData.message.imageMessage?.caption) {
  messageContent = messageData.message.imageMessage.caption;
} else if (messageData.message.conversation) {
  messageContent = messageData.message.conversation;
}

// Convertir fecha
const messageDate = new Date(webhookData.date_time).toISOString();

// Retornar UN SOLO item con todos los campos limpios
if (estaEnListaBlanca) {
  return [{
    json: {
      // Campos de instancia
      'instance.server_url': webhookData.server_url,
      'instance.nombre': webhookData.instance,
      'instance.apikey': webhookData.apikey,

      // Campos de mensaje
      'message.from': messageData.key.remoteJid,
      'message.id': messageData.key.id,
      'message.content_type': contentType,
      'message.content': messageContent,
      'message.date': messageDate,

      // Campos de usuario
      'user.name': messageData.pushName,

      // API Keys
      'keyapi.deepgram': '87347040d82c105d3f47de19f7582ae2bf341754',
      'keyapi.llamacloud': 'llx-8YVJLHZdOWSL0J4CEfDP7sV9UrgiLNZygPKB2S4RYbj51vVL',

      // Validación
      '_validacion': {
        estaEnListaBlanca: true,
        numeroValidado: numeroWebhook
      },

      // Mantener datos originales completos por si acaso
      '_original': $('recibe whatsapp').item.json
    }
  }];
} else {
  return [{
    json: {
      'instance.server_url': webhookData.server_url,
      'instance.nombre': webhookData.instance,
      'instance.apikey': webhookData.apikey,
      'message.from': messageData.key.remoteJid,
      'message.id': messageData.key.id,
      'message.content_type': contentType,
      'message.content': messageContent,
      'message.date': messageDate,
      'user.name': messageData.pushName,
      'keyapi.deepgram': '87347040d82c105d3f47de19f7582ae2bf341754',
      'keyapi.llamacloud': 'llx-8YVJLHZdOWSL0J4CEfDP7sV9UrgiLNZygPKB2S4RYbj51vVL',
      '_validacion': {
        estaEnListaBlanca: false,
        numeroValidado: numeroWebhook
      },
      '_original': $('recibe whatsapp').item.json
    }
  }];
}

// Nodo Set - Respuesta Saludo
const hora = new Date().getHours();
let saludo = '¡Hola';

if (hora < 12) saludo = '¡Buenos días';
else if (hora < 19) saludo = '¡Buenas tardes';
else saludo = '¡Buenas noches';

return [{
  json: {
    ...$json,
    mensaje_usuario: `${saludo}! 👋

Soy tu asistente de control de gastos.

Puedes decirme cosas como:
- "Pagué 25 en almuerzo"
- "50 soles de taxi"

O escribe *ayuda* para ver todas las opciones 😊`
  }
}];

