// Nodo Set - Respuesta NO_CLARO
return [{
  json: {
    ...$json,
    mensaje_usuario: `🤔 No logré entender bien tu mensaje.

Puedes decirme con más detalle qué deseas hacer.
Por ejemplo:
- "Registrar gasto de 20 soles en transporte"
- "Mostrar mis gastos de esta semana"
- "Ayuda" para ver cómo funciona el bot.

¿Podrías aclararlo un poco, por favor? 😊`
  }
}];

// Obtenemos los datos del nodo "BuscarGasto" (ajusta el nombre exacto)
const gasto = $node["get pendientes"].json;

// Extraemos los campos del gasto
const { monto, categoria, descripcion, fecha } = gasto;

// Función para convertir fecha de YYYY-MM-DD a DD/MM/YYYY
function formatearFecha(fecha) {
  if (!fecha) return 'Hoy';

  // Si ya viene en formato DD/MM/YYYY, retornarla tal cual
  if (fecha.includes('/')) return fecha;

  // Convertir de YYYY-MM-DD a DD/MM/YYYY
  const partes = fecha.split('-');
  if (partes.length === 3) {
    return `${partes[2]}/${partes[1]}/${partes[0]}`;
  }

  return fecha; // Si no coincide con ningún formato, devolver original
}

// Creamos el mensaje de confirmación con negritas (asterisco simple)
const mensaje = `✅ Perfecto, he registrado tu gasto correctamente.

💰 *Monto:* ${monto ? monto + ' soles' : 'No especificado'}
🏷️ *Categoría:* ${categoria || 'No especificada'}
📝 *Descripción:* ${descripcion || 'No especificada'}
📅 *Fecha:* ${formatearFecha(fecha)}

Gracias por confirmar. 🎉`;

return [{
  json: {
    ...$json,
    mensaje_usuario: mensaje
  }
}];

// Nodo Set - Respuesta CANCELAR
return [{
  json: {
    ...$json,
    mensaje_usuario: `❌ Entendido, he cancelado el registro del gasto.

No se ha guardado el gasto.  
Si quieres volver a intentarlo, puedes decirme algo como:
- "Registrar gasto de 40 soles en comida"
- "Agregar gasto en transporte"
`
  }
}];

// Obtener todos los items que vienen del nodo anterior
const items = $input.all();

// Si no hay items o el primer objeto no tiene claves => vacío
const tieneDatos = items.length > 0 && Object.keys(items[0].json || {}).length > 0;

return [
  {
    json: {
      tiene_datos: tieneDatos
    }
  }
];

// Obtener todos los items que vienen del nodo anterior
const items = $input.all();

// Si no hay items o el primer objeto no tiene claves => vacío
const tieneDatos = items.length > 0 && Object.keys(items[0].json || {}).length > 0;

return [
  {
    json: {
      tiene_datos: tieneDatos
    }
  }
];

// Nodo Code reporte — Generar respuesta de reporte (funciona con varios items entrantes)
// Obtener todos los items entrantes al nodo (cada item tiene .json)
const items = $input.all();
// Mapear a un array simple de objetos JSON
const data = items.map(i => i.json);

// Si no hay registros válidos
if (!Array.isArray(data) || data.length === 0 || Object.keys(data[0] || {}).length === 0) {
  return [{
    json: {
      ...$json,
      mensaje_usuario: "📭 No encontré gastos en ese período.",
      total: 0,
      resumen: {},
      registros: []
    }
  }];
}

// Función para convertir fecha de YYYY-MM-DD a DD/MM/YYYY
function formatearFecha(fecha) {
  if (!fecha) return '';

  // Si ya viene en formato DD/MM/YYYY, retornarla tal cual
  if (fecha.includes('/')) return fecha;

  // Convertir de YYYY-MM-DD a DD/MM/YYYY
  const partes = fecha.split('-');
  if (partes.length === 3) {
    return `${partes[2]}/${partes[1]}/${partes[0]}`;
  }

  return fecha; // Si no coincide con ningún formato, devolver original
}

// Calcular total y agrupar por categoría
let total = 0;
const resumen = {};
for (const g of data) {
  const monto = Number(g.monto) || 0;
  total += monto;
  const cat = g.categoria || "Sin categoría";
  resumen[cat] = (resumen[cat] || 0) + monto;
}

// Obtener periodo si existe (ej: 'hoy', 'ayer', etc.)
const periodo = $json.periodo || ($json.output && $json.output.reporte) || '';

// Construir mensaje con formato WhatsApp (asterisco simple)
let mensaje = `📊 *Reporte de gastos${periodo ? ' ' + periodo : ''}*\n`;
mensaje += `Total general: 💰 ${total.toFixed(2)} soles\n\n`;
mensaje += `*Por categorías:*\n`;
for (const [categoria, suma] of Object.entries(resumen)) {
  mensaje += `- ${categoria}: ${suma.toFixed(2)} soles\n`;
}

mensaje += `\n🧾 *Detalles:*\n`;
for (const g of data) {
  const desc = g.descripcion || 'Sin descripción';
  const fecha = formatearFecha(g.fecha || '');
  mensaje += `• ${desc} — ${Number(g.monto).toFixed(2)} soles (${g.categoria || 'Sin categoría'}, *${fecha}*)\n`;
}

mensaje += `\nSe encontraron ${data.length} registros en total.`;

// ========== NUEVA SECCIÓN: CONSTRUIR URL CON TOKEN ==========
// Obtener el token generado (viene del nodo "guardar token" de NocoDB)
const token = $('rangos y token').first().json.token;

// Construir la URL del dashboard con el token
const baseUrl = 'https://proxy.eficiencia.cloud/dashboard_gastos';
const dashboardUrl = `${baseUrl}?token=${token}`;

// Agregar el enlace al mensaje
mensaje += `\n\n🔗 *Reporte interactivo:*\n${dashboardUrl}`;
mensaje += `\n\n⚠️ _Este enlace es de un solo uso y expira en 24 horas._`;

// Retornar resultado para siguientes nodos
return [{
  json: {
    ...$json,
    mensaje_usuario: mensaje,
    total,
    resumen,
    registros: data,
    dashboard_url: dashboardUrl,  // URL completa para usar en otros nodos si es necesario
    token: token  // Token por si lo necesitas en otro nodo
  }
}];

