// Obtener las fechas desde la salida del agente
const fechaInicioAgente = $json.output?.fecha_inicio || 'ninguno';
const fechaFinAgente = $json.output?.fecha_fin || 'ninguno';
const descripcion = $json.output?.descripcion || 'sin descripción';

// Teléfono del usuario (ajusta según tu flujo)
const telefono = $('set fields').first().json._validacion.numeroValidado;

// Función para obtener fecha actual en hora de Perú (UTC-5)
function getPeruDate() {
  const now = new Date();
  const peruTime = new Date(now.getTime() - (5 * 60 * 60 * 1000));
  return peruTime;
}

// Función para formatear fecha y hora (YYYY-MM-DD HH:MM:SS)
function formatDateTime(date) {
  const year = date.getUTCFullYear();
  const month = String(date.getUTCMonth() + 1).padStart(2, '0');
  const day = String(date.getUTCDate()).padStart(2, '0');
  const hours = String(date.getUTCHours()).padStart(2, '0');
  const minutes = String(date.getUTCMinutes()).padStart(2, '0');
  const seconds = String(date.getUTCSeconds()).padStart(2, '0');
  return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
}

// Obtener fecha actual
const hoy = getPeruDate();

// Validar fechas (por si vienen como "ninguno")
const fechaInicio = fechaInicioAgente !== 'ninguno' ? fechaInicioAgente : null;
const fechaFin = fechaFinAgente !== 'ninguno' ? fechaFinAgente : null;

// Construir el WHERE para NocoDB
let where = `(telefono,eq,${telefono})`;
if (fechaInicio && fechaFin) {
  where += `~and(fecha,ge,exactDate,${fechaInicio})~and(fecha,le,exactDate,${fechaFin})`;
}

// Generar token seguro de 32 caracteres
const crypto = require('crypto');
const token = crypto.randomBytes(32).toString('hex');

// Crear objeto a encriptar
const datosParaEncriptar = JSON.stringify({
  token: token,
  telefono: telefono,
  fechaInicio: fechaInicio,
  fechaFin: fechaFin,
  descripcion: descripcion
});

// Encriptar usando Base64 URL-safe
const token_mas_datos = Buffer.from(datosParaEncriptar).toString('base64')
  .replace(/\+/g, '-')  // URL-safe
  .replace(/\//g, '_')  // URL-safe
  .replace(/=/g, '');   // Sin padding

// Retornar datos al flujo
return [{
  json: {
    ...$json,
    token,
    token_mas_datos,
    where,
    descripcion,
    fechaInicio,
    fechaFin,
    fechaHoraActual: formatDateTime(hoy)
  }
}];

// Nodo Code - Respuesta Despedida
const hora = new Date().getHours();
let despedida = '¡Hasta luego';

if (hora < 12) despedida = '¡Que tengas un excelente día';
else if (hora < 19) despedida = '¡Que tengas una linda tarde';
else despedida = '¡Que tengas una buena noche';

const despedidas = [
  `${despedida}! 👋\n\nCualquier cosa que necesites, aquí estaré para ayudarte. ¡Cuídate! 😊`,
  `${despedida}! 🌟\n\n¡Nos vemos pronto! Recuerda que puedes escribirme cuando quieras. 💙`,
  `${despedida}! ✨\n\nFue un gusto ayudarte. ¡Hasta la próxima! 😊`,
  `${despedida}! 🎉\n\n¡Que todo te salga genial! Aquí estaré cuando me necesites. 🤗`
];

// Seleccionar una despedida aleatoria
const mensajeDespedida = despedidas[Math.floor(Math.random() * despedidas.length)];

return [{
  json: {
    ...$json,
    mensaje_usuario: mensajeDespedida
  }
}];

// Nodo Code - Respuesta Agradecimiento
const agradecimientos = [
  `¡De nada! 😊\n\nEstoy aquí para ayudarte cuando lo necesites. ¡Que tengas un excelente día! 🌟`,
  `¡Un placer ayudarte! 💙\n\nSi necesitas algo más, solo dímelo. ¡Estoy aquí para ti! 😊`,
  `¡Para eso estoy! 🎉\n\nNo dudes en escribirme si necesitas ayuda con tus gastos. ¡Feliz día! ✨`,
  `¡Con gusto! 🤗\n\nCualquier cosa que necesites, aquí me tienes. ¡Que todo te vaya genial! 💫`,
  `¡Siempre a la orden! 😊\n\nRecuerda que puedes consultarme lo que sea. ¡Buen día! 🌈`
];

// Seleccionar un agradecimiento aleatorio
const mensajeAgradecimiento = agradecimientos[Math.floor(Math.random() * agradecimientos.length)];

return [{
  json: {
    ...$json,
    mensaje_usuario: mensajeAgradecimiento
  }
}];
